import axios from 'axios';

// Detectar se estamos no Codespaces ou local
const isCodespaces = window.location.hostname.includes('github.dev') || window.location.hostname.includes('codespaces');
const API_BASE_URL = isCodespaces 
  ? `https://expert-winner-97545wxw66jrc777j-8080.app.github.dev/api/pessoas`
  : 'http://localhost:8080/api/pessoas';

// Log para debug
console.log('ðŸ” Ambiente detectado:', isCodespaces ? 'Codespaces' : 'Local');
console.log('ðŸŒ URL da API:', API_BASE_URL);

class CarroService {
  constructor() {
    this.api = axios.create({
      baseURL: API_BASE_URL,
      headers: {
        'Content-Type': 'application/json',
      },
    });
  }

  // Listar todos os carros
  async listarCarros() {
    try {
      const response = await this.api.get('');
      // Adaptar dados de pessoa para carro
      const carros = response.data.map(pessoa => ({
        id: pessoa.id,
        modelo: pessoa.nome, // Usando o campo nome como modelo
        ano: pessoa.idade,   // Usando o campo idade como ano
        marca: `Marca ${pessoa.id}` // Gerando marca baseada no ID
      }));
      return carros;
    } catch (error) {
      console.error('Erro ao listar carros:', error);
      throw error;
    }
  }

  // Buscar carro por ID
  async buscarCarroPorId(id) {
    try {
      const response = await this.api.get(`/${id}`);
      const pessoa = response.data;
      // Adaptar dados de pessoa para carro
      return {
        id: pessoa.id,
        modelo: pessoa.nome,
        ano: pessoa.idade,
        marca: `Marca ${pessoa.id}`
      };
    } catch (error) {
      console.error(`Erro ao buscar carro com ID ${id}:`, error);
      throw error;
    }
  }

  // Criar novo carro
  async criarCarro(carro) {
    try {
      // Adaptar dados de carro para pessoa
      const pessoa = {
        nome: carro.modelo,
        idade: carro.ano
      };
      const response = await this.api.post('', pessoa);
      // Adaptar resposta de pessoa para carro
      return {
        id: response.data.id,
        modelo: response.data.nome,
        ano: response.data.idade,
        marca: `Marca ${response.data.id}`
      };
    } catch (error) {
      console.error('Erro ao criar carro:', error);
      throw error;
    }
  }

  // Deletar carro
  async deletarCarro(id) {
    try {
      await this.api.delete(`/${id}`);
      return true;
    } catch (error) {
      console.error(`Erro ao deletar carro com ID ${id}:`, error);
      throw error;
    }
  }
}

export default new CarroService();
